<!-- Generate another component part like this by running command `rails generate vue something` -->

<template>
  <div class="transaction-form">
    <span v-if="asked" class="open_petition button-action" @click="toggleShow">Pedir</span>
    <transition>
      <form v-if="show" class="form" :action="$createTransactionPath" method="post" @submit="sendPetition">
        <input name="utf8" type="hidden" value="✓">
        <input type="hidden" name="authenticity_token" :value="csrfToken">
        <input type="hidden" name="transaction[service_id]" :value="serviceId">
        <div class="field">
          <label for="datetime">Horario: </label><br>
          <input type="text" name="transaction[datetime]" id="datetime"
                 placeholder="Horario: eg. 07/07/2020 13:00" cols="40">
        </div>
        <div class="field">
          <label for="addition_information">Información aditional: </label><br>
          <textarea name="transaction[addition_information]" id="addition_information"
                    placeholder="Añade información extra" rows="3" cols="40"></textarea>
        </div>
        <div class="actions">
          <button class="send_petition">Enviar</button>
        </div>
      </form>
    </transition>
  </div>
</template>

<script>
  import axios from 'axios'
  import railsFlash from "../railsFlash";
  import formParams from '../formHelper'

  export default {
    props: ['serviceId', 'service_asked'],
    data: function() {
      return {
        show: false,
        csrfToken: '',
        asked: true
      };
    },
    mounted() {
      this.csrfToken = this.$getCSRFToken()
      console.log(this.service_asked)
      if (this.service_asked){
        this.asked = false
      }
    },
    methods: {
      toggleShow: function () {
        this.show = !this.show
      },
      sendPetition: function (e) {
        e.preventDefault()
        axios.post(e.target.action, formParams.getPostParams(e.target.elements))
                .then(response => {
                  railsFlash.notice(response.data.message)
                  this.asked = false
                })
                .catch(err => railsFlash.alert(err.data.message))
      }
    }
  };
</script>

<style scoped>
  textarea {
    resize:  none;
  }

  input, textarea {
    font-size: 1rem;
  }
</style>