<template>
  <div class="transaction-form dropdown">
    <ul v-if="haveErrors" class="errors">
      <li
        v-for="(error, name, index) in errors"
        :key="index"
        :class="`error_${name}`"
      >
        {{ error }}
      </li>
    </ul>
    <form
      class="service-petition border-top mt-2"
      :action="$createTransactionPath"
      method="post"
      @submit.prevent="sendPetition"
    >
      <input name="utf8" type="hidden" value="✓" />
      <input type="hidden" name="authenticity_token" :value="$getCSRFToken()" />
      <input type="hidden" name="transaction[service_id]" :value="service_id" />
      <div class="field">
        <DatetimePicker @selected-time="setDurationRange"></DatetimePicker>
      </div>
      <div class="field form-group">
        <label for="transaction-duration">Duración: </label>
        <div>
          <input
            type="number"
            min="0"
            max="23"
            placeholder="Número de horas"
            :value="duration"
            @input="validateInteger"
            @keydown.delete="handleDurationDelete"
            name="transaction[duration]"
            id="transaction-duration"
            class="form-control"
          />
          <span class="duration-time-range">{{ rangeDuration }}</span>
        </div>
      </div>
      <div class="field form-group">
        <label for="addition_information">Información aditional:</label>
        <br />
        <textarea
          class="form-control"
          name="transaction[addition_information]"
          id="addition_information"
          placeholder="Añade información extra"
          rows="3"
          cols="40"
        ></textarea>
      </div>
      <div class="actions">
        <button class="send_petition btn btn-primary">Enviar</button>
      </div>
    </form>
  </div>
</template>

<script>
import axios from "axios";
import railsFlash from "../railsFlash";
import formParams from "../formHelper";
import DatetimePicker from "./DatetimePicker/DatetimePicker";
import _ from "lodash/lang";

export default {
  props: {
    service_id: {
      type: Number,
      required: true,
    },
  },
  components: {
    DatetimePicker,
  },
  data: function() {
    return {
      errors: {},
      duration: 0,
      rangeDuration: "Selecciona una hora para ver el rango",
    };
  },
  computed: {
    haveErrors() {
      return !_.isEmpty(this.errors);
    },
  },
  methods: {
    formatMinutes(minutes) {
      return minutes < 10 ? `0${minutes}` : minutes;
    },
    toHourAndMinutes(seconds) {
      const hours = seconds / (60 * 60);
      const minutes = this.formatMinutes((seconds - hours * (60 * 60)) / 60);
      return `${hours}:${minutes}`;
    },
    setDurationRange(timeInSeconds) {
      const start = this.toHourAndMinutes(timeInSeconds);
      const end = this.toHourAndMinutes(timeInSeconds + 2 * 60 * 60);
      this.rangeDuration = `${start} - ${end}`;
    },
    validateInteger(event) {
      const value = parseFloat(event.target.value);
      if (_.isInteger(value) && value <= 23) {
        this.duration = value;
      }
      this.$forceUpdate();
    },
    handleDurationDelete() {
      if (this.duration < 10) {
        this.duration = 0;
      }
    },
    sendPetition: function(e) {
      axios
        .post(
          this.$createTransactionPath,
          formParams.getPostParams(e.target.elements)
        )
        .then((response) => {
          this.errors = {};
        })
        .catch((err) => {
          railsFlash.alert(err.data.message);
          this.errors = err.data.errors;
        });
    },
  },
};
</script>

